nginx_config() {
    cat << EOF
server {
    listen 8042 default_server;
    listen [::]:8042 default_server;

    charset utf-8;
    index index.php;
    root /var/servidor/public;

    add_header X-Frame-Options "SAMEORIGIN";
    add_header X-XSS-Protection "1; mode=block";
    add_header X-Content-Type-Options "nosniff";

    location / {
        try_files \$uri \$uri/ /index.php?\$query_string;
    }

    location ~ \.php\$ {
        fastcgi_pass unix:/run/php/php7.4-fpm.sock;
        fastcgi_index index.php;
        fastcgi_param SCRIPT_FILENAME \$document_root\$fastcgi_script_name;
        include fastcgi_params;
    }

    location = /favicon.ico {
        access_log off;
        log_not_found off;
    }

    error_page 404 /index.php;
}
EOF
}

nginx_default_page() {
    cat << EOF
<!DOCTYPE html>
<html>
  <head>
    <title>Site Not Found</title>

    <style type="text/css">
    h1, h2 {
      margin: 0;
    }
    h1 {
      font-size: 200px;
    }
    hr {
      margin: 50px 0;
    }
    .box {
      font-family: sans-serif;
      margin: 0 auto;
      max-width: 1000px;
      min-width: 650px;
      position: absolute;
      transform: translate(-50%, -50%);
      top: 40%;
      left: 50%;
      width: 50%;
      background-position: 100% 35px;
      background-repeat: no-repeat;
      background-image: url(data:image/png;base64,);
    }
    </style>
  </head>

  <body>
    <div class="box">
      <h1>404</h1>
      <h2>This site doesn't seem to exist yet.</h2>
      <hr>
      <h3>Is this your website?</h3>
      <p>Good news! Your domain is pointed at Servidor.</p>
      <p>Once you've added this domain in Applications, enable it in the Site Editor to go live.</p>
    </div>
  </body>
</html>
EOF
}
